import { __decorate } from "tslib";
import { Component } from '@angular/core';
let HomeComponent = class HomeComponent {
  constructor() {
    this.texto = "Este es un texto con estilo que se muestra letra por letra";
    this.longitudTextoMostrado = 0;
    this.mostrarCursor = true;
  }
  ngOnInit() {
    this.mostrarTexto();
  }
  mostrarTexto() {
    if (this.longitudTextoMostrado < this.texto.length) {
      this.longitudTextoMostrado++;
      setTimeout(() => {
        this.mostrarTexto();
        this.mostrarCursor = !this.mostrarCursor; // alternar el cursor
      }, 200);
    }
  }
};
